import React, { useState } from 'react'
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import '../styles/main.css';
import '../styles/sign.css';
import '../styles/font-awesome.min.css';
import '../styles/bootstrap.min.css';
import '../styles/bootstrap-theme.css';
import '../styles/card.css';

const Signin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState('');
  const history = useHistory();

  const Auth = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/login', {
        email: email,
        password: password
      });
      history.push("/");
    } catch (error) {
      if (error.response) {
        setMsg(error.response.data.msg);
      }
    }
  }

  return (
    <><ol class="breadcrumb">
      <center>
        <li><a href="/">Home</a></li>
        <li class="active">User access</li>
      </center>
    </ol>

      <div class="row">
        <article class="col-xs-12 maincontent">

          <div class="col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2">
            <div class="panel panel-default">
              <div class="panel-body">
                <h3 class="thin text-center">Sign in to your account</h3>
                <p class="text-center text-muted">Login jika sudah memiliki akun, <a href="/signup">Register</a>
                  Jika belum silahkan daftarkan akunmu.</p>
                <hr></hr>

                <form onSubmit={Auth}>
                  <p className='has-text-centered'>{msg}</p>
                  <div class="top-margin">
                    <label>Email <span class="text-danger">*</span></label>
                    <input type="text" class="form-control" placeholder='email' value={email} onChange={(e) => setEmail(e.target.value)}></input>
                  </div>
                  <div class="top-margin">
                    <label>Password <span class="text-danger">*</span></label>
                    <input type="password" class="form-control" placeholder='*****' value={password} onChange={(e) => setPassword(e.target.value)}>
                    </input>
                  </div>

                  <hr></hr>

                  <div class="row">
                    <div class="col-lg-4 text-right">
                      <button class="btn btn-action" type="submit">Sign in</button>
                    </div>
                  </div>
                </form>
              </div>
            </div>

          </div>

        </article>

      </div></>

  )
}

export default Signin
