import React, { useState, useEffect } from 'react'
import axios from 'axios';
import jwt_decode from "jwt-decode";
import { useHistory } from 'react-router-dom';
import '../styles/main.css';
import '../styles/sign.css';
import '../styles/font-awesome.min.css';
import '../styles/bootstrap.min.css';
import '../styles/bootstrap-theme.css';
import '../styles/card.css';


const Order = () => {
  const [confirmations, setConfirmations] = useState([]);

  useEffect(() => {
    getConfirmations();
  }, []);

  const getConfirmations = async () => {
    const response = await axios.get('http://localhost:5000/confirmations');
    setConfirmations(response.data);
  }
  return (
    <>
      <div class="box-puppy">
        <img src="./images/puppy.jpg" alt="puppy" class="box-img" />
      </div>
      <div className='dataUser'>
        {confirmations.map((confirmation) => (
          <><h1>Nama: {confirmation.fullName}</h1><h1>Tanggal Pernikahan: {confirmation.tgl_Wedding}</h1>
            <h1>Biaya Maksimum yang dikeluarkan: {confirmation.biayaMaksimum}</h1>
            <h1>Catatan Tambahan: {confirmation.catatanTambahan}</h1></>
        ))}
      </div>
      <center>
        <div class="box-report">
          <br></br>
          <form class="report-day1">
            <label for="w3review">Laporan Harian</label>
            <textarea id="w3review" name="w3review" rows="4" cols="50">
              At w3schools.com you will learn how to make a website. They offer free tutorials in all web development technologies.
            </textarea>
            <br></br>
          </form>
          <form class="report-day">
            <label for="w3review">Laporan Harian</label>
            <textarea id="w3review" name="w3review" rows="4" cols="50">
              At w3schools.com you will learn how to make a website. They offer free tutorials in all web development technologies.
            </textarea>
            <br></br>
          </form>
          <form class="report-day">
            <label for="w3review">Laporan Harian</label>
            <textarea id="w3review" name="w3review" rows="4" cols="50">
              At w3schools.com you will learn how to make a website. They offer free tutorials in all web development technologies.
            </textarea>
            <br></br>
          </form>
          <form class="report-day">
            <label for="w3review">Laporan Harian</label>
            <textarea id="w3review" name="w3review" rows="4" cols="50">
              At w3schools.com you will learn how to make a website. They offer free tutorials in all web development technologies.
            </textarea>
            <br></br>
          </form>
        </div>
      </center>

      <footer>

        <div class="footer1">
          <div class="container">
            <center>
              <h4>Copyright PreWedding - Capstone 2021</h4>
            </center>
          </div>
        </div>

      </footer></>
  )
}

export default Order;
