/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react'
import axios from 'axios';
import jwt_decode from "jwt-decode";
import { useHistory } from 'react-router-dom';
import '../styles/main.css';

const Dashboard = () => {
    const [name, setName] = useState('');
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState('');
    const [users, setUsers] = useState([]);
    const history = useHistory();

    useEffect(() => {
        refreshToken();
        getUsers();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:5000/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                history.push("/");
            }
        }
    }

    const axiosJWT = axios.create();

    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expire * 1000 < currentDate.getTime()) {
            const response = await axios.get('http://localhost:5000/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const getUsers = async () => {
        const response = await axiosJWT.get('http://localhost:5000/users', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
        setUsers(response.data);
    }

    return (
        <><div className="navbar navbar-inverse navbar-fixed-top headroom">
            <div class="container">
                <div class="navbar-header">
                    <button type="button" class="navbar-toggle" data-toggle="collapse" data-target=".navbar-collapse"><span
                        class="icon-bar"></span> <span class="icon-bar"></span> <span class="icon-bar"></span> </button>
                    <a class="navbar-brand" href="/">PreWedding</a>
                </div>
                <div id="navigationDrawer">
                    <div class="navbar-collapse collapse">
                        <ul class="nav navbar-nav pull-right">
                            <li class="active"><a href="/">Home</a></li>
                            <li><a href="/about">About</a></li>
                            <li><a class="btn" href="/signin">SIGN IN / SIGN UP</a></li>
                        </ul>
                    </div>
                </div>
            </div>
        </div><br></br>
            <br></br>
            <hr></hr>
            <div className="container mt-5">
                <h1>Welcome Back {name}</h1>
                <h2>Pilih Wedding Organizer Favorit Kamu</h2>
                <center>
                    <div class="area-box">
                        <div class="card">
                            <div class="card-header">
                                <img src="./images/puppy.jpg" alt="puppy" />
                            </div>
                            <div class="card-body">
                                <a href="#/puppy">Puppy Wedding</a>
                                <p>
                                    We make your wedding with puppy decoration. Wait for you
                                </p>
                                <button onClick={getUsers} className="button is-info">Order Jasa</button>
                            </div>
                        </div>

                        <div class="card">
                            <div class="card-header">
                                <img src="./images/pinkwedding.jpg" alt="pink" />
                            </div>
                            <div class="card-body">
                                <a href="#/pink">Pink Wedding</a>
                                <p>
                                    We will make your wedding with elegant pink vibes.
                                </p>
                                <button onClick={getUsers} className="button is-info">Order Jasa</button>
                            </div>
                        </div>
                    </div>
                </center>
                <button onClick={getUsers} className="button is-info">Get Users</button>
                <table className="table is-striped is-fullwidth">
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Name</th>
                            <th>Email</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user, index) => (
                            <tr key={user.id}>
                                <td>{index + 1}</td>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                            </tr>
                        ))}

                    </tbody>
                </table>
            </div></>
    )
}

export default Dashboard
