import React, { useState, useEffect } from 'react'
import axios from 'axios';
import '../styles/order.css';
import '../styles/main.css';
import '../styles/sign.css';
import '../styles/font-awesome.min.css';
import '../styles/bootstrap.min.css';
import '../styles/bootstrap-theme.css';
import '../styles/card.css';


const History = () => {
  const [confirmations, setConfirmations] = useState([]);

  useEffect(() => {
    getConfirmations();
  }, []);

  const getConfirmations = async () => {
    const response = await axios.get('http://localhost:5000/confirmations');
    setConfirmations(response.data);
  }
  return (
    <>
      <div className='order-area'>
        <div className='data'>
          {confirmations.map((confirmation) => (
            <><div class="dataUser">

              <div class="table">
                <div class="table-header">
                  <div class="header__item"><a id="name" class="filter__link" href="#">Nama Lengkap</a></div>
                  <div class="header__item"><a id="wins" class="filter__link filter__link--number" href="#">Tanggal Pernikahan</a></div>
                  <div class="header__item"><a id="draws" class="filter__link filter__link--number" href="#">Biaya Maksimum</a></div>
                  <div class="header__item"><a id="losses" class="filter__link filter__link--number" href="#">Catatan Tambahan</a></div>
                  <div class="header__item"><a id="losses" class="filter__link filter__link--number" href="#">Detail</a></div>
                </div>
                <div class="table-content">
                  <div class="table-row">
                    <div class="table-data">{confirmation.fullName}</div>
                    <div class="table-data">{confirmation.tgl_Wedding}</div>
                    <div class="table-data">{confirmation.biayaMaksimum}</div>
                    <div class="table-data">{confirmation.catatanTambahan}</div>
                    <div class="table-data"><a href="/order"><button type="button">Detail</button></a></div>
                  </div>
                </div>
              </div>
            </div></>
          ))}
        </div>
      </div>

      <footer>

        <div class="footer1">
          <div class="container">
            <center>
              <h4>Copyright PreWedding - Capstone 2021</h4>
            </center>
          </div>
        </div>

      </footer></>
  )
}

export default History;
